window.Thunder.components["customer-login"].template=function(r){return'<div class="thunder--login-form-wrapper"><h2>'+r.m("login")+'</h2><form class="thunder--login-form thunder--form"><div class="thunder--customer-user-id-or-email"><label for="userIdOrEmail">'+r.m("userIdOrEmail")+'</label><input type="text" name="userIdOrEmail" placeholder="'+r.m("userIdOrEmail")+'" required></div><div class="thunder--customer-password"><label for="password">'+r.m("password")+'</label><input type="password" name="password" placeholder="'+r.m("password")+'" required><div class="thunder--password-action"><a class="thunder--go-to-reset-password">'+r.m("goToResetPassword")+'</a></div></div><div class="thunder--form-actions"><button type="submit" class="thunder--login-customer thunder--button">'+r.m("doLogin")+"</button></div></form>"+r.social()+"</div>"};