window.Thunder.components["customer-verification"].template=function(e){var r='<div class="thunder--reset-password-form-wrapper"><h2>'+e.m("verifyEmail")+'</h2><form class="thunder--reset-password-form thunder--form">';return"request-email"===e.type&&(r+='<div class="thunder--customer-user-id-or-email"><label for="userIdOrEmail">'+e.m("userIdOrEmail")+'</label><input type="text" name="userIdOrEmail" placeholder="'+e.m("userIdOrEmail")+'" required></div><div class="thunder--form-actions"><button type="submit" class="thunder--request-email thunder--button">'+e.m("requestEmail")+"</button></div>"),"verification"===e.type&&(r+='<div class="thunder--form-actions"><button type="submit" class="thunder--finish-verification thunder--button">'+e.m("finishVerification")+"</button></div>"),r+="</form></div>"};