window.Thunder.components["product-review-writer"].template=function(e){var t='<div class="thunder--review-writer"><form class="thunder--write-review-form thunder--form"><input type="hidden" name="product" value="'+e.options.product+'">';return e.options.useRating&&(t+='<div class="thunder--product-review-rating"><label>'+e.m("reviewRating")+"</label>"+e.ui["review-star-rating"](5)+"</div>"),t+='<div class="thunder--product-review-title"><label>'+e.m("reviewTitle")+'</label><input type="text" name="title" placeholder="'+e.m("reviewTitle")+'"></div><div class="thunder--product-review-body"><label>'+e.m("reviewBody")+'</label><textarea name="body" placeholder="'+e.m("reviewBody")+'"></textarea></div></form><form class="thunder--review-image-form thunder--form"><div class="thunder--review-images"></div><div class="thunder--review-image-inputs"><label for="thunder--review-image">'+e.m("addImage")+'</label><input type="hidden" name="model" value="Review"><input type="hidden" name="application" value="images"><input type="file" name="file" id="thunder--review-image" class="hidden"></div></form><div class="thunder--form-actions"><button class="thunder--post-product-review thunder--button">'+e.m("postReview")+'</button><button class="thunder--cancel-product-review thunder--button skeleton">'+e.m("cancelReview")+"</button></div></div>"};